UseTemplate.UPDATE_CODE:
(A:GEAR HANDLE POSITION, Bool) (A:GEAR POSITION, percent) 99.9 > and (>O:_ShouldBeConnectedToPowerGrid)
(O:_ShouldBeConnectedToPowerGrid) sp0
1 (>A:BUS LOOKUP INDEX, Number)
(A:CIRCUIT CONNECTION ON:51, Bool) l0 != if{ 51 1 (>K:2:ELECTRICAL_BUS_TO_CIRCUIT_CONNECTION_TOGGLE) }
(A:CIRCUIT CONNECTION ON:54, Bool) l0 != if{ 54 1 (>K:2:ELECTRICAL_BUS_TO_CIRCUIT_CONNECTION_TOGGLE) }

(A:GENERAL ENG MIXTURE LEVER POSITION:1, Percent over 100) 0.9 < if{
	(>K:MIXTURE_RICH)
}

UseTemplate.EMISSIVE_CODE:
(A:SIM ON GROUND, Bool) if{
(A:ELECTRICAL BATTERY LOAD, Amps) 0 >
} els{
(A:ELECTRICAL MASTER BATTERY, Bool) !
}

UseTemplate.ANIM_CODE:
            (A:ATTITUDE INDICATOR PITCH DEGREES:1, degrees) abs 90 > if{
                0.5 (A:ATTITUDE INDICATOR PITCH DEGREES:1, degrees) /-/ * 90 +
	        } els{
                1.5 (A:ATTITUDE INDICATOR PITCH DEGREES:1, degrees) /-/ * 180 +
            }
